  const [product,setProduct] = useState({});

  const {id} = useParams();
  const getProductDetails = async () => {
    const response = await axios.get(`http://127.0.0.1:8000/api/${id}`);
    console.log(response.data);
    setProduct(response.data);
  }

  useEffect(() => {
    getProductDetails();
  },[]);

